let ai1, ai2;

try {
  ai1 = $input.first().json.output_a0 || "";
} catch {
  ai1 = "";
}

try {
  ai2 = $input.first().json.output_rev || "";
} catch {
  ai2 = "";
}

// If there's nothing new, just return the original
if (!ai2.trim()) {
  return [{ json: { output: ai1.trim() } }];
}

// 1) Figure out ai2’s section number
const m = ai2.match(/^\s*(\d+)\./);
const ai2Num = m ? parseInt(m[1], 10) : null;

// 2) Split ai1 into chunks at each "X. **Name**:"  
//    Any leading intro text will be chunk[0] without a number
const chunks = ai1
  .split(/(?=\d+\. \*\*Name\*\*: )/g)
  .map(s => s.trim())
  .filter(s => s.length);

// 3) Replace matching section or mark for append
let replaced = false;
const updated = chunks.map(chunk => {
  const numMatch = chunk.match(/^\s*(\d+)\./);
  const num = numMatch ? parseInt(numMatch[1], 10) : null;

  if (num !== null && num === ai2Num) {
    replaced = true;
    return ai2.trim();
  }
  return chunk;
});

// 4) If no replace happened and ai2Num is valid, append it
if (!replaced && ai2Num !== null) {
  updated.push(ai2.trim());
}

// 5) Sort all numbered chunks by their section number; keep any non-numbered intro at front
function chunkKey(chunk) {
  const m = chunk.match(/^\s*(\d+)\./);
  return m ? parseInt(m[1], 10) : -1;  // intro text gets -1 and stays first
}

updated.sort((a, b) => chunkKey(a) - chunkKey(b));

// 6) Join with two line breaks
const merged = updated.join("\n\n").trim();

return [
  {
    json: {
      output: merged
    }
  }
];

