return items.map(item => {
  const score = item.json.GesamtscoreNum;
  let category;

  // Falls du die Kategorie nicht nochmal brauchst, kannst du diesen Teil weglassen,
  // da 'Bewertung' ja schon als Text da ist.
  if (score >= 100 && score <= 117) {
    category = 'sehr_gut';
  } else if (score >= 90 && score <= 109) {
    category = 'solide';
  } else if (score >= 65 && score <= 89) {
    category = 'schwach';
  } else {
    category = 'kritisch';
  }

  return {
    json: {
      ...item.json,
      Bewertung: item.json.Bewertung,
      Zusammenfassung: item.json.Zusammenfassung,
      GesamtscoreNum: score,
      category,  // Optional, falls du es brauchst
    }
  };
});

return [
  {
    json: {
      result: items[0].json.output_1 + items[0].json.output_2 + items[0].json.output_3 + items[0].json.output_4 + items[0].json.output_5 + items[0].json.output
    }
  }
];

// Get the raw string from the first item
let raw = items[0].json.result;

// 1. Remove all ```json or ``` markers
raw = raw.replace(/```json|```/g, '');

// 2. Remove standalone "json" lines (not inside values)
raw = raw.replace(/\bjson\b/g, '');

// 3. Trim outer whitespace
raw = raw.trim();

// Optional: remove leading/trailing curly braces IF you really want that
// WARNING: This may break JSON structure if used improperly.
raw = raw.replace(/^\{+|\}+$/g, '');

// Return cleaned version
return [
  {
    json: {
      cleaned: raw
    }
  }
];
